import { useState } from 'react';
import './Main.css';
// import { useToggle } from "@uidotdev/usehooks";

const Main = () =>{
   
  const bioData = [
        {id:1,name:"Akash" , score:"23"},
          {id:2,name:"Dimple", score:"22"},
    ];
   const InitialBtnText = "Update";
      const [myArray, setmyArray] = useState(bioData);
      const [toggle, setToggle] = useState(true);
      const [btntext, setText] = useState(InitialBtnText);

      // handling forms
      const [sid, setId] = useState("");
      const [fname, setFname] = useState("");
      const [score,setScore] = useState("");
  
  const handleName = (e) => {
    setFname(e.target.value);
  };

   const handleID = (e) =>{
    setId(parseInt(e.target.value));
  }

   const handleScore = (e) =>{
  setScore(e.target.value);
  }


    
      const clearArray = () =>{
      setmyArray([]);
    }

      const handleClick = () => {
      setToggle(!toggle);
     if(!toggle){
      setText(InitialBtnText);
      console.log("running");
     }else{
      setText('Cancel'); 
     }  
  };


    const updateArray = () =>{
     
      const isFound = myArray.some(el => el.id === sid);
  
      console.log(isFound);
  
    if(isFound){
    setmyArray(myArray.map(obj =>{

        if(obj.id === sid){
          return {...obj,name: fname,score : score}
        }else{
       return obj;
      }
     
    }));
  }else{
     const new_data = {
          id : sid,
          name: fname,
          score: score
     }
     setmyArray([...myArray,new_data])
  
  }
  



    // setmyArray([...myArray,newState]);
 
    };



    return (
      <div className='main'>
      <h1 className='c-head'>Todo List</h1>
  
        {myArray.map((currElm) => <h2 className="head" key={currElm.id}><span className="name elm">Hello {currElm.name} </span> <span className='score elm'> Score : {currElm.score}</span> 
           <button className='removebtn btn'>Remove</button>
          </h2>)
        }

        {!toggle && <div className="input-container" >
        <input type="number" placeholder='Please enter your ID' className='inputelm' onChange={handleID} />

         <input 
        type="text"  placeholder='Please write your Name' className='inputelm' onChange={handleName} />
        
        <input 
        type="number"  placeholder='Please write your Score' className='inputelm' onChange={handleScore} />
         <button className='addbtn btn inputelm' onClick={updateArray}>Add</button>

        </div>}
           <div className='btnContainer'>
          <button className="btn" onClick={function(event){  handleClick()}}>{btntext}</button>
        <button className="btn" onClick={function(event){clearArray()}}> Clear</button>
        </div>
     </div>
    );

}


export default Main;
