import React, { useState } from "react";
import './Main.css';

const Main = () => {

    const arrayBio = [
        {Id:1,name:"Akash",age:"25"},
        {Id:2,name:"Dimple",age:"23"},
];

    const [myArray,setArray] = useState(arrayBio);

    const onArrClick = () =>{
        setArray([]);
    }

  return (
    <div className='main'>
    <h1 className='c-head'>Todo List</h1>
    
    {myArray.map((currElm) => <h2 className="head" key={currElm.id}><span className="name elm">Hello {currElm.name} </span> <span className='age elm'> Age : {currElm.age}</span> 
    <button className='removebtn btn'>Remove</button>
   </h2>)
 }
    <button className='clrbtn btn' onClick={onArrClick}>Clear</button>

        
    
    </div>
  )

}

export default Main
