import React, { useState } from "react";
import './Main.css';

const Main = () => {

    const arrayBio = [
        {id:0,name:"Akash",age:25},
        {id:1,name:"Dimple",age:23},
];

    const [myArray,setArray] = useState(arrayBio);

    const onArrClear = () =>{
        setArray([]);
    }

    const removeElm = (id) =>{
        const myNewArray = myArray.filter((currElm)=>{
            return currElm.id != id;
        })
    }

  return (
    <div className='main'>
    <h1 className='c-head'>Todo List</h1>
    
    { 
    myArray.map((currElm) =>{

    return ( 
        
     <h2 className="head" key={currElm.id}><span className="name elm">Hello {currElm.name} </span>
         <span className='age elm'> Age : {currElm.age}</span>
     <button className='removebtn btn' onClick={() => removeElm(currElm.id)}>Remove</button>
   </h2>);
   
    })
  }
    <button className='clrbtn btn' onClick={onArrClear}>Clear</button>

        
    
    </div>
  )

}

export default Main
